pragma solidity ^0.5.0;


interface IERC1056 {
  function changeOwnerSigned(
    address identity,
    uint8 sigV,
    bytes32 sigR,
    bytes32 sigS,
    address newOwner
  ) external;

  function changeOwner(address identity, address newOwner) external;

  function identityOwner(address identity) external;
}


contract OfferableIdentity {
  address public owner;
  address erc1056;
  string public id;

  uint8 sigV;
  bytes32 sigR;
  bytes32 sigS;
  address offeredTo;

  event OfferableIdentityCreated(address identity, address owner);
  event IdentityOffered(address identity, address offeredTo);
  event IdentityTransferred(address identity, address owner);
  event OfferRejected(address identity, address offeredTo);

  constructor(string memory _id, address _erc1056, address _owner) public {
    id = _id;
    erc1056 = _erc1056;
    IERC1056 registry = IERC1056(_erc1056);
    registry.changeOwner(address(this), _owner);
    owner = _owner;
    emit OfferableIdentityCreated(address(this), owner);
  }

  modifier isOwner() {
    require(msg.sender == owner, "Only owner allowed");
    _;
  }

  modifier isOfferedTo() {
    require(offeredTo != address(0), "Proxy is not offered");
    require(msg.sender == offeredTo, "Proxy offered to other account");
    _;
  }

  function offer(address _offerTo, uint8 _sigV, bytes32 _sigR, bytes32 _sigS)
    external
    isOwner
  {
    offeredTo = _offerTo;
    sigV = _sigV;
    sigR = _sigR;
    sigS = _sigS;
    emit IdentityOffered(address(this), offeredTo);
  }

  function accept() external isOfferedTo {
    IERC1056(erc1056).changeOwnerSigned(
      address(this),
      sigV,
      sigR,
      sigS,
      offeredTo
    );
    owner = offeredTo;
    emit IdentityTransferred(address(this), offeredTo);
    closeOffer();
  }

  function reject() external isOfferedTo {
    emit OfferRejected(address(this), offeredTo);
    closeOffer();
  }

  function closeOffer() internal {
    offeredTo = address(0);
    sigV = 0;
    sigR = "";
    sigS = "";
  }
}
